function varargout = funcslider(varargin)
% FUNCSLIDER Function Slider GUI
%      
%      FUNCSLIDER is a utility to vary input arguments (up to 5)
%      continuously using slider bars, and then evaluating a function based
%      on the output. The default limits of the slider bars are [0 -> 1],
%      but may be changed.
%      Input Function is a function handle (it must start with @).
%      The structure of the function will be analyzed to give an assumed
%      format of [z1,z2,..] = f(x1,x2,...) based on the number of input
%      and output arguments.
%      Output function is any valid MATLAB expression involving the output
%      variables [z1,z2,...]
%       
%      FUNCSLIDER starts the GUI populating the fields with demo values.
%
%      FUNCSLIDER(F) starts the GUI with function handle F as the input
%      function, and an empty output function.
%
%      FUNCSLIDER(F,S) starts the GUI with function handle F as the input
%      function, and string S as the output function.
%
%      NOTE: Any of these values may be changed from inside the GUI.
%
%      EXAMPLES:
%        1. Running funcslider by itself will give a default example using
%           an anonymous function
%    
%             funcslider
%
%        2. This is an example using a surface plot:
%
%             funcslider(@example1,'surf(z1); camlight')
%
%        3. Here is an example that uses multiple output values:
%
%             funcslider(@example2,'plot(z1,z2)')
%
%        4. An example that uses three input values:
%
%             funcslider(@example3,'stem(z1)')
%
%        5. Here is another example using anonymous functions and data from
%           the workspace:
%
%             I = rand(101,101,101);
%             funcslider(@(k) I(:,:,1+round(100*k)) ,'imagesc(z1)')
%
%
%
%

%   Teja Muppirala
%   Copyright 2010 The MathWorks, Inc.
%   Version 1.0 2010/6/30

% Begin initialization code - DO NOT EDIT
gui_Singleton = 1;
gui_State = struct('gui_Name',       mfilename, ...
                   'gui_Singleton',  gui_Singleton, ...
                   'gui_OpeningFcn', @funcslider_OpeningFcn, ...
                   'gui_OutputFcn',  @funcslider_OutputFcn, ...
                   'gui_LayoutFcn',  @funcslider_LayoutFcn, ...
                   'gui_Callback',   []);
if nargin && ischar(varargin{1})
    gui_State.gui_Callback = str2func(varargin{1});
end

if nargout
    [varargout{1:nargout}] = gui_mainfcn(gui_State, varargin{:});
else
    gui_mainfcn(gui_State, varargin{:});
end
% End initialization code - DO NOT EDIT


% --- Executes just before funcslider is made visible.
function funcslider_OpeningFcn(hObject, eventdata, handles, varargin)
% This function has no output args, see OutputFcn.
% hObject    handle to figure
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
% varargin   command line arguments to funcslider (see VARARGIN)
if isempty(varargin)
    set(handles.edit_in,'string','@(x) x*sin(x*[1:100])')
    set(handles.edit_out,'string','plot(z1); grid on');
else
    fin = varargin{1};
    try; set(handles.edit_out,'string',varargin{2}); end
    fstring = func2str(fin);
    if fstring(1) ~= '@', fstring = ['@' fstring]; end
    set(handles.edit_in,'string',fstring);
end

fin = get(handles.edit_in,'string');

set(gcf,'units','normalized','interruptible','off');
set(gcf,'Name','Function Slider');

%set(handles.axes1,'nextplot','replacechildren');

for k = 1:5
    eval(['s(' num2str(k) ') = handles.slider' num2str(k) ';']);
end
setappdata(gcf,'s',s);

initialize(fin,handles);

% Choose default command line output for funcslider
handles.output = hObject;

% Update handles structure
guidata(hObject, handles);

% UIWAIT makes funcslider wait for user response (see UIRESUME)
% uiwait(handles.figure1);


