function slidermove(handles)
    s = getappdata(gcbf,'s');
    invals = getappdata(gcbf,'invals');
    outvals = getappdata(gcbf,'outvals');
    s = s(1:invals);
    
    p = get(s,'value');
    if isequal(p, get(gcf,'userdata'))
        return
    end
    
    if get(handles.checkbox1,'value')
        curxlim = xlim;
    end
    if get(handles.checkbox2,'value')
        curylim = ylim;
    end
    if get(handles.checkbox3,'value')
        curzlim = zlim;
    end
    if get(handles.checkbox4,'value')
        ct0 = get(gca,'cameratarget');
        cu0 = get(gca,'cameraupvector');
        cp0 = get(gca,'cameraposition');
        cv0 = get(gca,'cameraview');
        da0 = get(gca,'dataaspectratio');
        [AZ,EL] = view;
    end
    
    h_out = handles.edit_out;
    
    fin = getappdata(gcf,'fin');
    fstring = '[z1';
    for k = 2:outvals
        fstring = [fstring ',z' num2str(k)];
    end
    
    try
    if iscell(p)
        %z = feval(fin,p{:});
        eval([fstring '] = feval(fin,p{:});']);
    else
        %z = feval(fin,p);
        eval([fstring '] = feval(fin,p);']);
    end
    catch, disp(lasterr)
    end
    
    set(handles.text1,'string',evalc('whos z*'));
    
    
    
    try; eval(get(h_out,'string')); catch; disp(lasterr); end
    
    %Adjust the limits if needed
    if get(handles.checkbox1,'value')
        xlim(curxlim)
    end
    if get(handles.checkbox2,'value')
        ylim(curylim);
    end
    if get(handles.checkbox3,'value')
        zlim(curzlim);
    end
    if get(handles.checkbox4,'value')
        view(AZ,EL);
        set(gca,'cameratarget',ct0);
        set(gca,'cameraupvector',cu0);
        set(gca,'cameraposition',cp0);
        set(gca,'cameraview',cv0);
        set(gca,'dataaspectratio',da0);
    end
    
    drawnow;

    for k = 1:invals
        hsli = eval(['handles.slider' num2str(k)]);
        hval = eval(['handles.edit_val' num2str(k)]);
        set(hval,'string',num2str(get(hsli,'value')));
    end
    
    set(gcf,'userdata',p);
        
function initialize(fin,handles)
if fin(1) ~= '@';
    errordlg('The input function should start with ''@''')
end
try fin = evalin('base',fin);
catch
        errordlg(lasterr);
        return
end
setappdata(gcf,'fin',fin);

invals = nargin(fin);
outvals = abs(nargout(fin));
setappdata(gcf,'invals',invals);
setappdata(gcf,'outvals',outvals);
s = getappdata(gcf,'s');

set(s(1:invals),'enable','on');
set(s(invals+1:5),'enable','off');

fstring = '(x1';
for k = 2:invals
    fstring = [fstring ',x' num2str(k)];
end
rhs = [fstring ')'];

fstring = '[z1';
for k = 2:outvals
    fstring = [fstring ',z' num2str(k)];
end
lhs = [fstring ']'];

set(handles.text14,'string',['Function structure:   ' lhs ' = f' rhs]);



