function figure1_WindowButtonMotionFcn(hObject, eventdata, handles)
slidermove(handles);

function adj_sliderlevels(handles,num)

hmin = eval(['handles.edit_min' num2str(num)]);
hmax = eval(['handles.edit_max' num2str(num)]);
hval = eval(['handles.edit_val' num2str(num)]);
hsli = eval(['handles.slider' num2str(num)]);

newmin = str2double(get(hmin, 'string'));
if ~isnumeric(newmin) || ~isfinite(newmin),
    newmin = 0;
end
newmax = str2double(get(hmax, 'string'));
if ~isnumeric(newmin) || ~isfinite(newmax),
    newmax = 0;
end
newval = str2double(get(hval, 'string'));
if ~isnumeric(newmin) || ~isfinite(newmax),
    newval = newmin;
end
if newval > newmax, newmax = newval;
    set(hmax,'string',num2str(newmax));
elseif newval < newmin, newmin = newval;
    set(hmin,'string',num2str(newmin));
end

set(hsli, 'min', newmin, 'max', newmax,'value',newval);

